package com.javarush.island.mokropolov;import com.javarush.island.mokropolov.game.Game;import com.javarush.island.mokropolov.game.GamePreference;import com.javarush.island.mokropolov.game.threads.MainWorker;import com.javarush.island.mokropolov.game.map.GameMap;import com.javarush.island.mokropolov.game.map.GameMapCreator;import com.javarush.island.mokropolov.game.view.ConsoleView;public class Runner{    public static void main(String[] args) {        Factory entityFactory = new Factory();        GameMapCreator gameMapCreator = new GameMapCreator(entityFactory);        int rows = GamePreference.ROWS;        int cols = GamePreference.COLS;        GameMap gameMap = gameMapCreator.createRandomFilledGameMap(rows, cols, false);        ConsoleView view=new ConsoleView(gameMap);        Game game = new Game(gameMap, entityFactory, view);        MainWorker mainWorker = new MainWorker(game);        mainWorker.start();    }}