package com.javarush.island.mokropolov.game;public class EntityPrefs {    private final String name;    private final String image;    private final double maxWeight;    private final int maxCount;    private final int speed;    private final double foodWeight;    private double weight;    public EntityPrefs(String name, String image, double maxWeight, int maxCount, int speed, double foodWeight) {        this.name = name;        this.image = image;        this.maxWeight = maxWeight;        this.maxCount = maxCount;        this.speed = speed;        this.foodWeight = foodWeight;        this.weight = getWeight();    }    public double getWeight() {        return Randomizer.random(this.maxWeight /2, this.maxWeight);    }    public double getFoodWeight() {        return foodWeight;    }    public double getMaxWeight() {        return maxWeight;    }    public String getName() {        return name;    }    public String getImage() {        return image;    }    public int getMaxCount() {        return maxCount;    }    public int getSpeed() {        return speed;    }}