package com.javarush.island.mokropolov.game;import com.javarush.island.mokropolov.Factory;import com.javarush.island.mokropolov.game.map.GameMap;import com.javarush.island.mokropolov.game.view.ConsoleView;import com.javarush.island.mokropolov.game.view.View;public class Game {    private final GameMap gameMap;    private final Factory entitiesFactory;    private final ConsoleView view;    public Game(GameMap gameMap, Factory entitiesFactory, ConsoleView view) {        this.gameMap = gameMap;        this.entitiesFactory = entitiesFactory;        this.view = view;    }    public GameMap getGameMap() {        return gameMap;    }    public Factory getEntitiesFactory() {        return entitiesFactory;    }    public View getView() {        return view;    }}