package com.javarush.island.mokropolov.game.view;import com.javarush.island.mokropolov.game.map.Cell;import com.javarush.island.mokropolov.game.map.GameMap;import java.util.*;public class ConsoleView implements View{    public final GameMap gameMap;    public ConsoleView(GameMap gameMap) {        this.gameMap = gameMap;    }    @Override    public String showStatistics() {        Map<String, Integer> statistics = new HashMap<>();        Cell[][] cells = gameMap.getCells();        for (Cell[] row : cells) {            for (Cell cell : row) {                var cellsEntities = cell.getCellsEntities();                if (Objects.nonNull(cellsEntities)) {                    cellsEntities.values().stream()                            .filter(set -> set.size() > 0)                            .forEach(set -> {                                        String icon = set.stream().findAny().get().getImage();                                        statistics.put(icon, statistics.getOrDefault(icon, 0) + set.size());                                    }                            );                }            }        }        System.out.println(statistics.toString().replaceAll("[{}]","")+ "\n");        return statistics.toString();    }}